sect2.programlisting:
// Using getItem():
$item = $data->getItem('foo');

// Or use array notation:
$item = $data['foo'];

// Update or create an item:
$data['foo'] = array('title' => 'Foo', 'email' => 'foo@foo.com');

// Delete an item:
unset($data['foo']);

// Test for an item:
if (isset($data[foo])) {
}
